parameters.jsCode:
// Analyze the issues and categorize by severity
const issueData = $input.first().json;
const answer = issueData.answer || '';
const confidence = issueData.confidence || 0;

// Determine severity based on keywords
let severity = 'low';
if (answer.toLowerCase().includes('critical') || answer.toLowerCase().includes('urgent')) {
  severity = 'critical';
} else if (answer.toLowerCase().includes('overdue') || answer.toLowerCase().includes('stuck')) {
  severity = 'high';
} else if (answer.toLowerCase().includes('delay') || answer.toLowerCase().includes('issue')) {
  severity = 'medium';
}

// Extract any order numbers mentioned
const orderMatches = answer.match(/order[s]?\s*#?(\d+)/gi) || [];
const orderNumbers = orderMatches.map(match => {
  const num = match.match(/\d+/);
  return num ? parseInt(num[0]) : null;
}).filter(Boolean);

// Create alert data
const alertData = {
  timestamp: new Date().toISOString(),
  severity: severity,
  confidence: confidence,
  issue_description: answer,
  affected_orders: orderNumbers,
  alert_id: `ALERT_${Date.now()}`,
  requires_immediate_action: severity === 'critical',

  // Format for different notification channels
  notifications: {
    slack: {
      color: severity === 'critical' ? '#ff0000' : severity === 'high' ? '#ff8800' : '#ffaa00',
      emoji: severity === 'critical' ? 'ðŸš¨' : severity === 'high' ? 'âš ï¸' : 'ðŸ“‹',
      title: `${severity.toUpperCase()} Order Issues Detected`
    },
    email: {
      priority: severity === 'critical' ? 'High' : 'Normal',
      subject: `${severity.toUpperCase()}: Order Monitoring Alert`
    }
  }
};

return alertData;

