// Extract and validate incoming data
const body = $input.first().json.body;

// Extract core information
const question = body.question || body.message || '';
const customer_id = body.customer_id || body.customer_number || null;
const order_number = body.order_number || body.order_id || null;
const source = body.source || 'unknown';
const priority = body.priority || 'normal';

// Extract metadata
const metadata = {
  timestamp: new Date().toISOString(),
  source: source,
  channel: body.channel || 'api',
  session_id: body.session_id || null,
  user_agent: body.user_agent || null
};

// Build context
const context = {
  source: source,
  priority: priority,
  metadata: metadata,
  ...body.context
};

// Validate required fields
if (!question || question.trim() === '') {
  throw new Error('Question is required');
}

// Return formatted data
return {
  question: question.trim(),
  customer_id: customer_id,
  order_number: order_number,
  context: context,
  priority: priority,
  source: source
};

// Process AI agent response
const agentResponse = $input.first().json;
const originalData = $('Extract & Validate Data').first().json;

// Extract response details
const answer = agentResponse.answer || 'No response generated';
const confidence = agentResponse.confidence || 0;
const suggestions = agentResponse.suggestions || [];
const dataSources = agentResponse.data_sources || [];

// Determine response quality
let responseQuality = 'good';
if (confidence < 0.6) {
  responseQuality = 'poor';
} else if (confidence < 0.8) {
  responseQuality = 'fair';
}

// Check if human escalation is needed
const requiresHuman = confidence < 0.7 || 
                     answer.toLowerCase().includes('error') ||
                     answer.toLowerCase().includes('sorry') ||
                     originalData.priority === 'urgent';

// Format final response
const formattedResponse = {
  status: 'success',
  answer: answer,
  confidence: Math.round(confidence * 100),
  confidence_level: responseQuality,
  suggestions: suggestions,
  data_sources: dataSources.join(', '),
  requires_human: requiresHuman,
  processing_time: new Date().toISOString(),
  source: originalData.source,

  // Metadata for analytics
  metadata: {
    original_question: originalData.question,
    customer_id: originalData.customer_id,
    order_number: originalData.order_number,
    response_quality: responseQuality,
    workflow_version: '1.0'
  }
};

// Add formatted text for easy consumption
let responseText = `**Answer:** ${answer}\n\n`;
if (confidence > 0) {
  responseText += `**Confidence:** ${Math.round(confidence * 100)}%\n`;
}
if (suggestions.length > 0) {
  responseText += `\n**Follow-up Options:**\n${suggestions.map(s => `‚Ä¢ ${s}`).join('\n')}`;
}
if (requiresHuman) {
  responseText += `\n\n*This query has been flagged for human review.*`;
}

formattedResponse.formatted_text = responseText;

return formattedResponse;

